const data = JSON.parse($('Start the Senna Project').first().json.stdout)

// Validar que data existe y tiene info_institutions
if (!data || !data.info_institutions || !Array.isArray(data.info_institutions)) {
  console.log('Error: info_institutions no existe o no es un array');
  return { error: 'info_institutions no encontrado' };
}

// Filtrar solo los elementos con perfil_individual: true
const perfilIndividualItems = data.info_institutions.filter(item => item.perfil_individual === true);
console.log(perfilIndividualItems)

// Sumar todas las deudas de esos elementos
const totalDeuda = perfilIndividualItems.reduce((sum, item) => sum + item.divida, 0);

return {
  total_debt_amount: Math.round(totalDeuda)
}

