const meses = {
  "janeiro": 1, "fevereiro": 2, "marco": 3, "abril": 4, "maio": 5, "junho": 6,
  "julho": 7, "agosto": 8, "setembro": 9, "outubro": 10, "novembro": 11, "dezembro": 12
};

const now = new Date();
const currentMonth = now.getMonth() + 1;
const currentYear = now.getFullYear();

return items.map(item => {
  const mesmapaStr = (item.json.mesmapa || "").toLowerCase().replace("de", "");
  let caducado = true;

  for (const [mesNome, mesNumero] of Object.entries(meses)) {
    if (mesmapaStr.includes(mesNome)) {
      const ano = parseInt(mesmapaStr.replace(/\D/g, '').slice(-4));
      if (!isNaN(ano)) {
        const diffMeses = (currentYear - ano) * 12 + (currentMonth - mesNumero);
        caducado = diffMeses > 2;  // ‚¨ÖÔ∏è Mapa com mais de 2 meses est√° "caducado"
      }
      break;
    }
  }

  return {
    json: {
